import{_ as a,c as t,o as s,ag as i}from"./chunks/framework.BHpayLOB.js";const u=JSON.parse('{"title":"搜索引擎常用技巧","description":"","frontmatter":{"title":"搜索引擎常用技巧","date":"2020-04-27T14:31:32.000Z","prev":{"text":"CSS 手册","link":"/post/css-book"},"next":{"text":"CSS 不正交","link":"/post/css-not-orthogonal"}},"headers":[],"relativePath":"post/search-skill.md","filePath":"post/search-skill.md","lastUpdated":1714895308000}'),o={name:"post/search-skill.md"};function l(n,e,p,d,c,r){return s(),t("div",null,e[0]||(e[0]=[i('<h2 id="在某个网站内搜索关键词" tabindex="-1">在某个网站内搜索关键词 <a class="header-anchor" href="#在某个网站内搜索关键词" aria-label="Permalink to &quot;在某个网站内搜索关键词&quot;">​</a></h2><p>在知乎中搜索 360 安全，可以在搜索栏 (或地址栏) 输入：</p><div class="language- vp-adaptive-theme"><button title="Copy Code" class="copy"></button><span class="lang"></span><pre class="shiki shiki-themes github-light github-dark vp-code" tabindex="0"><code><span class="line"><span>site:zhihu.com 360 安全</span></span></code></pre></div><h2 id="筛选无用的关键词" tabindex="-1">筛选无用的关键词 <a class="header-anchor" href="#筛选无用的关键词" aria-label="Permalink to &quot;筛选无用的关键词&quot;">​</a></h2><p>搜索 知乎 时不想看到 &quot;发现&quot; 关键词，可以在知乎后空格再加一个 <code>-发现</code>，搜索框输入如下 (注意A后面的<strong>空格不能省略</strong>)：</p><div class="language- vp-adaptive-theme"><button title="Copy Code" class="copy"></button><span class="lang"></span><pre class="shiki shiki-themes github-light github-dark vp-code" tabindex="0"><code><span class="line"><span>知乎 -发现</span></span></code></pre></div><h2 id="星号模糊搜索" tabindex="-1">星号模糊搜索 <a class="header-anchor" href="#星号模糊搜索" aria-label="Permalink to &quot;星号模糊搜索&quot;">​</a></h2><p>很多时候想搜一个东西但是不确定具体名字，可以用星号代替忘了的字，例如 (表示搜索泽野弘之)：</p><div class="language- vp-adaptive-theme"><button title="Copy Code" class="copy"></button><span class="lang"></span><pre class="shiki shiki-themes github-light github-dark vp-code" tabindex="0"><code><span class="line"><span>泽*弘之</span></span></code></pre></div><h2 id="完全匹配结果搜索" tabindex="-1">完全匹配结果搜索 <a class="header-anchor" href="#完全匹配结果搜索" aria-label="Permalink to &quot;完全匹配结果搜索&quot;">​</a></h2><p><code>&quot;&quot;</code> 双引号表示完全匹配，结果中必须出现与搜索文本完全相同的内容。</p><div class="language- vp-adaptive-theme"><button title="Copy Code" class="copy"></button><span class="lang"></span><pre class="shiki shiki-themes github-light github-dark vp-code" tabindex="0"><code><span class="line"><span>&quot;大家一起喜羊羊&quot;</span></span></code></pre></div><h2 id="搜索对应类型的文件" tabindex="-1">搜索对应类型的文件 <a class="header-anchor" href="#搜索对应类型的文件" aria-label="Permalink to &quot;搜索对应类型的文件&quot;">​</a></h2><p><code>filetype</code> 搜索对应类型的文件。例如：</p><div class="language- vp-adaptive-theme"><button title="Copy Code" class="copy"></button><span class="lang"></span><pre class="shiki shiki-themes github-light github-dark vp-code" tabindex="0"><code><span class="line"><span>中国防火墙 filetype:ppt</span></span></code></pre></div><p>即为搜索包含主题中国防火墙的ppt文件。（请注意<strong>使用英文的冒号</strong>）</p><hr><p>其他：</p><ul><li><code>link</code> 搜索网页中含有某链接的结果，比如搜索：<code>link:http://baidu.com</code>，则结果为包含百度这个链接的页面。</li><li><code>intitle</code> 搜索标题内包含关键词的结果。</li><li><code>inurl</code> 搜索网站地址中包含关键词的结果。</li><li><code>related</code> 搜索相关网站。</li><li><code>index of</code> 可以突破网站入口下载。</li></ul><p>还有一些，比如<code>allintitle,allinurl</code>，都很少用，主要是前面四个，再加一个通配符 <code>*</code>。</p>',20)]))}const b=a(o,[["render",l]]);export{u as __pageData,b as default};
